import { Send, Paperclip, Mic, Image as ImageIcon, X, MoreVertical, Sparkles } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { useState, useRef, KeyboardEvent, useEffect } from "react";

interface ChatInputProps {
  onSendMessage: (text: string, files?: File[], images?: string[]) => void;
  onVoiceClick: () => void;
  onImageGenClick: () => void;
  disabled?: boolean;
}

export function ChatInput({
  onSendMessage,
  onVoiceClick,
  onImageGenClick,
  disabled,
}: ChatInputProps) {
  const [message, setMessage] = useState("");
  const [attachedFiles, setAttachedFiles] = useState<File[]>([]);
  const [attachedImages, setAttachedImages] = useState<string[]>([]);
  const textareaRef = useRef<HTMLTextAreaElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const imageInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    const textarea = textareaRef.current;
    if (textarea) {
      textarea.style.height = "auto";
      const scrollHeight = textarea.scrollHeight;
      const maxHeight = window.innerWidth < 768 
        ? window.innerHeight * 0.7 
        : window.innerHeight * 0.5;
      textarea.style.height = Math.min(scrollHeight, maxHeight) + "px";
    }
  }, [message]);

  const handleSend = () => {
    if (message.trim() || attachedFiles.length > 0 || attachedImages.length > 0) {
      onSendMessage(message, attachedFiles, attachedImages);
      setMessage("");
      setAttachedFiles([]);
      setAttachedImages([]);
      if (textareaRef.current) {
        textareaRef.current.style.height = "auto";
      }
    }
  };

  const handleKeyDown = (e: KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && e.ctrlKey) {
      e.preventDefault();
      handleSend();
    }
  };

  const handleFileSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    setAttachedFiles((prev) => [...prev, ...files]);
  };

  const handleImageSelect = (e: React.ChangeEvent<HTMLInputElement>) => {
    const files = Array.from(e.target.files || []);
    files.forEach((file) => {
      const reader = new FileReader();
      reader.onload = (e) => {
        if (e.target?.result) {
          setAttachedImages((prev) => [...prev, e.target!.result as string]);
        }
      };
      reader.readAsDataURL(file);
    });
  };

  const removeFile = (index: number) => {
    setAttachedFiles((prev) => prev.filter((_, i) => i !== index));
  };

  const removeImage = (index: number) => {
    setAttachedImages((prev) => prev.filter((_, i) => i !== index));
  };

  return (
    <div className="sticky bottom-0 border-t bg-background px-6 py-4">
      <div className="mx-auto max-w-3xl">
        {(attachedFiles.length > 0 || attachedImages.length > 0) && (
          <div className="mb-3 flex flex-wrap gap-2">
            {attachedImages.map((img, idx) => (
              <div key={`img-${idx}`} className="relative h-20 w-20" data-testid={`attached-image-${idx}`}>
                <img
                  src={img}
                  alt={`Preview ${idx}`}
                  className="h-full w-full rounded-md object-cover"
                />
                <Button
                  size="icon"
                  variant="destructive"
                  className="absolute -right-2 -top-2 h-6 w-6"
                  onClick={() => removeImage(idx)}
                  data-testid={`button-remove-image-${idx}`}
                >
                  <X className="h-3 w-3" />
                </Button>
              </div>
            ))}
            {attachedFiles.map((file, idx) => (
              <div
                key={`file-${idx}`}
                className="flex h-20 items-center gap-2 rounded-md border bg-muted px-3"
                data-testid={`attached-file-${idx}`}
              >
                <Paperclip className="h-4 w-4" />
                <span className="max-w-32 truncate text-sm">{file.name}</span>
                <Button
                  size="icon"
                  variant="ghost"
                  className="h-6 w-6"
                  onClick={() => removeFile(idx)}
                  data-testid={`button-remove-file-${idx}`}
                >
                  <X className="h-3 w-3" />
                </Button>
              </div>
            ))}
          </div>
        )}

        <div className="flex items-end gap-2">
          <input
            ref={imageInputRef}
            type="file"
            accept="image/*"
            multiple
            className="hidden"
            onChange={handleImageSelect}
          />
          <input
            ref={fileInputRef}
            type="file"
            multiple
            className="hidden"
            onChange={handleFileSelect}
          />

          <DropdownMenu>
            <DropdownMenuTrigger asChild>
              <Button
                size="icon"
                variant="ghost"
                disabled={disabled}
                data-testid="button-attachment-menu"
              >
                <MoreVertical className="h-5 w-5" />
              </Button>
            </DropdownMenuTrigger>
            <DropdownMenuContent align="start" className="w-56">
              <DropdownMenuItem
                onClick={() => imageInputRef.current?.click()}
                data-testid="menu-upload-image"
              >
                <ImageIcon className="mr-2 h-4 w-4" />
                <span>Загрузить изображение</span>
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={() => fileInputRef.current?.click()}
                data-testid="menu-upload-file"
              >
                <Paperclip className="mr-2 h-4 w-4" />
                <span>Приложить файл</span>
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={onVoiceClick}
                data-testid="menu-voice-input"
              >
                <Mic className="mr-2 h-4 w-4" />
                <span>Голосовой ввод</span>
              </DropdownMenuItem>
              <DropdownMenuItem
                onClick={onImageGenClick}
                data-testid="menu-generate-image"
              >
                <Sparkles className="mr-2 h-4 w-4" />
                <span>Создать изображение</span>
              </DropdownMenuItem>
            </DropdownMenuContent>
          </DropdownMenu>

          <Textarea
            ref={textareaRef}
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyDown={handleKeyDown}
            placeholder="Введите сообщение..."
            className="min-h-[3rem] resize-none overflow-hidden flex-1"
            style={{ maxHeight: typeof window !== 'undefined' && window.innerWidth < 768 ? '70vh' : '50vh' }}
            disabled={disabled}
            data-testid="input-message"
          />

          <Button
            size="icon"
            onClick={handleSend}
            disabled={disabled || (!message.trim() && attachedFiles.length === 0 && attachedImages.length === 0)}
            data-testid="button-send-message"
          >
            <Send className="h-4 w-4" />
          </Button>
        </div>

        <p className="mt-2 text-center text-xs text-muted-foreground hidden md:block">
          Ctrl+Enter для отправки
        </p>
      </div>
    </div>
  );
}

