import { useState, useEffect, useRef } from "react";
import { SidebarTrigger } from "@/components/ui/sidebar";
import { ScrollArea } from "@/components/ui/scroll-area";
import { ChatMessage } from "@/components/ChatMessage";
import { ChatInput } from "@/components/ChatInput";
import { VoiceInput } from "@/components/VoiceInput";
import { ImageGenerator } from "@/components/ImageGenerator";
import { ThemeToggle } from "@/components/ThemeToggle";
import { Message, AIModel, Conversation } from "@shared/schema";
import { Bot, Loader2 } from "lucide-react";
import { sendMessage } from "@/lib/api";
import { storage } from "@/lib/storage";

interface ChatProps {
  conversation?: Conversation;
  selectedModel: AIModel;
  onConversationUpdate: () => void;
}

export default function Chat({ conversation, selectedModel, onConversationUpdate }: ChatProps) {
  const [messages, setMessages] = useState<Message[]>(conversation?.messages || []);
  const [voiceOpen, setVoiceOpen] = useState(false);
  const [imageGenOpen, setImageGenOpen] = useState(false);
  const [isStreaming, setIsStreaming] = useState(false);
  const [streamingText, setStreamingText] = useState("");
  const streamingTextRef = useRef<string>("");
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (conversation) {
      setMessages(conversation.messages);
      setStreamingText("");
      streamingTextRef.current = "";
      setIsStreaming(false);
    }
  }, [conversation?.id]);

  useEffect(() => {
    // Auto-scroll to bottom when new messages arrive
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages, streamingText]);

  const handleSendMessage = async (text: string, files?: File[], images?: string[]) => {
    if (!conversation) return;

    const userContent: any[] = [];

    if (text.trim()) {
      userContent.push({ type: "text", text });
    }

    if (images) {
      images.forEach((url) => {
        userContent.push({ type: "image", url });
      });
    }

    const userMessage: Message = {
      id: Date.now().toString(),
      role: "user",
      content: userContent,
      timestamp: new Date(),
    };

    // Save user message to localStorage
    storage.addMessage(conversation.id, userMessage);
    setMessages((prev) => [...prev, userMessage]);
    onConversationUpdate();

    setIsStreaming(true);
    setStreamingText("");
    streamingTextRef.current = "";

    await sendMessage({
      model: selectedModel,
      messages: [...messages, userMessage],
      content: userContent,
      images,
      files,
      onChunk: (chunk) => {
        streamingTextRef.current += chunk;
        setStreamingText(streamingTextRef.current);
      },
      onDone: () => {
        const aiMessage: Message = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: [{ type: "text", text: streamingTextRef.current }],
          model: selectedModel,
          timestamp: new Date(),
        };

        // Save AI message to localStorage
        storage.addMessage(conversation.id, aiMessage);
        setMessages((prev) => [...prev, aiMessage]);
        onConversationUpdate();

        setStreamingText("");
        streamingTextRef.current = "";
        setIsStreaming(false);
      },
      onError: (error) => {
        console.error("Error sending message:", error);
        setStreamingText("");
        streamingTextRef.current = "";
        setIsStreaming(false);
        const errorMessage: Message = {
          id: (Date.now() + 1).toString(),
          role: "assistant",
          content: [{ type: "text", text: "Извините, произошла ошибка при обработке вашего запроса." }],
          model: selectedModel,
          timestamp: new Date(),
        };

        // Save error message to localStorage
        storage.addMessage(conversation.id, errorMessage);
        setMessages((prev) => [...prev, errorMessage]);
        onConversationUpdate();
      },
    });
  };

  const handleVoiceTranscript = (text: string) => {
    handleSendMessage(text);
  };

  const handleImageGenerated = (url: string) => {
    handleSendMessage("", undefined, [url]);
  };

  return (
    <div className="flex h-screen flex-col">
      <header className="flex h-14 items-center justify-between border-b px-4">
        <div className="flex items-center gap-3">
          <SidebarTrigger data-testid="button-sidebar-toggle" />
          <div className="flex items-center gap-2">
            <Bot className="h-5 w-5" />
            <h1 className="font-semibold">AI Chat</h1>
          </div>
        </div>
        <ThemeToggle />
      </header>

      <ScrollArea className="flex-1" ref={scrollRef}>
        {messages.length === 0 && !isStreaming ? (
          <div className="flex h-full items-center justify-center">
            <div className="text-center">
              <Bot className="mx-auto mb-4 h-12 w-12 text-muted-foreground" />
              <h2 className="mb-2 text-xl font-semibold">
                Начните новый разговор
              </h2>
              <p className="text-sm text-muted-foreground">
                Выберите модель AI и отправьте сообщение для начала
              </p>
            </div>
          </div>
        ) : (
          <div className="space-y-0 py-6">
            {messages.map((message) => (
              <ChatMessage key={message.id} message={message} />
            ))}
            {isStreaming && streamingText && (
              <ChatMessage
                message={{
                  id: "streaming",
                  role: "assistant",
                  content: [{ type: "text", text: streamingText }],
                  model: selectedModel,
                  timestamp: new Date(),
                }}
              />
            )}
            {isStreaming && !streamingText && (
              <div className="flex gap-4 px-6 py-6">
                <div className="flex h-8 w-8 shrink-0 items-center justify-center rounded-md bg-muted">
                  <Bot className="h-5 w-5" />
                </div>
                <div className="flex items-center gap-2">
                  <Loader2 className="h-4 w-4 animate-spin" />
                  <span className="text-sm text-muted-foreground">Думаю...</span>
                </div>
              </div>
            )}
          </div>
        )}
      </ScrollArea>

      <ChatInput
        onSendMessage={handleSendMessage}
        onVoiceClick={() => setVoiceOpen(true)}
        onImageGenClick={() => setImageGenOpen(true)}
        disabled={isStreaming || !conversation}
      />

      <VoiceInput
        open={voiceOpen}
        onOpenChange={setVoiceOpen}
        onTranscript={handleVoiceTranscript}
      />

      <ImageGenerator
        open={imageGenOpen}
        onOpenChange={setImageGenOpen}
        onImageGenerated={handleImageGenerated}
      />
    </div>
  );
}

client/src/components/ConversationList.t.content:
import { MessageSquare, Trash2 } from "lucide-react";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Conversation } from "@shared/schema";
import { format, isToday, isYesterday, isThisWeek } from "date-fns";
import { ru } from "date-fns/locale";

interface ConversationListProps {
  conversations: Conversation[];
  selectedId?: string;
  onSelect: (id: string) => void;
  onDelete: (id: string) => void;
}

export function ConversationList({
  conversations,
  selectedId,
  onSelect,
  onDelete,
}: ConversationListProps) {
  const groupConversations = () => {
    const groups: { [key: string]: Conversation[] } = {
      today: [],
      yesterday: [],
      thisWeek: [],
      older: [],
    };

    conversations.forEach((conv) => {
      if (isToday(conv.updatedAt)) {
        groups.today.push(conv);
      } else if (isYesterday(conv.updatedAt)) {
        groups.yesterday.push(conv);
      } else if (isThisWeek(conv.updatedAt, { locale: ru })) {
        groups.thisWeek.push(conv);
      } else {
        groups.older.push(conv);
      }
    });

    return groups;
  };

  const groups = groupConversations();
  const groupLabels = {
    today: "Сегодня",
    yesterday: "Вчера",
    thisWeek: "На этой неделе",
    older: "Ранее",
  };

  return (
    <ScrollArea className="flex-1">
      <div className="space-y-6 px-4 py-4">
        {Object.entries(groups).map(([key, convs]) =>
          convs.length > 0 ? (
            <div key={key}>
